contracts/AssetToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

contract AssetToken is ERC20, Ownable, Pausable {
    using Math for uint256;

    address private _minter;
    uint256 private _totalDividends;
    mapping(address => uint256) private _dividendCredits;
    mapping(address => uint256) private _lastClaimedDividends;
    address[] private _holders;
    mapping(address => bool) private _isHolder;
    uint256 private constant PRECISION = 1e18;

    event DividendsDistributed(uint256 amount);
    event DividendsClaimed(address indexed holder, uint256 amount);

    error OnlyMinter();
    error NoDividendsToClaim();
    error InsufficientBalance();

    constructor(string memory name, string memory symbol) 
        ERC20(name, symbol)
        Ownable()
    {
        _transferOwnership(msg.sender);
    }

    function minter() public view returns (address) {
        return _minter;
    }

    function setMinter(address minterAddress) public onlyOwner {
        _minter = minterAddress;
    }

    function mint(address to, uint256 amount) public whenNotPaused {
        if (msg.sender != _minter) revert OnlyMinter();
        _mint(to, amount);
        if (!_isHolder[to]) {
            _holders.push(to);
            _isHolder[to] = true;
        }
    }

    function burn(address from, uint256 amount) public whenNotPaused {
        if (msg.sender != _minter) revert OnlyMinter();
        if (balanceOf(from) < amount) revert InsufficientBalance();
        _burn(from, amount);
    }

    function distributeDividends() public payable whenNotPaused {
        require(msg.value > 0, "No ETH sent");
        require(totalSupply() > 0, "No tokens minted");

        _totalDividends += msg.value;

        // Distribute dividends based on token count
        uint256 totalTokens = totalSupply();
        uint256 dividendPerToken = msg.value * PRECISION / totalTokens;

        for (uint256 i = 0; i < _holders.length; i++) {
            address holder = _holders[i];
            uint256 holderBalance = balanceOf(holder);
            if (holderBalance > 0) {
                _dividendCredits[holder] += (dividendPerToken * holderBalance) / PRECISION;
            }
        }

        emit DividendsDistributed(msg.value);
    }

    function claimDividends() public whenNotPaused {
        uint256 claimable = getClaimableDividends(msg.sender);
        if (claimable == 0) revert NoDividendsToClaim();

        _dividendCredits[msg.sender] = 0;
        _lastClaimedDividends[msg.sender] = _totalDividends;

        (bool success, ) = payable(msg.sender).call{value: claimable}("");
        require(success, "Transfer failed");

        emit DividendsClaimed(msg.sender, claimable);
    }

    function getClaimableDividends(address account) public view returns (uint256) {
        return _dividendCredits[account];
    }

    function getTotalDividends() public view returns (uint256) {
        return _totalDividends;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);

        if (from != address(0) && to != address(0)) {
            if (!_isHolder[to]) {
                _holders.push(to);
                _isHolder[to] = true;
            }

            // Transfer unclaimed dividends proportionally
            if (balanceOf(from) > 0) {
                uint256 unclaimedDividends = _dividendCredits[from];
                uint256 dividendsToTransfer = (unclaimedDividends * amount) / balanceOf(from);
                _dividendCredits[from] = unclaimedDividends - dividendsToTransfer;
                _dividendCredits[to] += dividendsToTransfer;
            }
        }
    }

    receive() external payable {
        distributeDividends();
    }
}

contracts/InvestmentManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./AssetToken.sol";
import "./ProjectRegistry.sol";

contract InvestmentManager is Ownable, Pausable {
    struct Investment {
        uint256 projectId;
        address investor;
        uint256 amount;
        uint256 tokens;
        uint256 timestamp;
    }

    AssetToken public assetToken;
    ProjectRegistry public projectRegistry;
    mapping(uint256 => Investment[]) public projectInvestments;
    mapping(address => mapping(uint256 => uint256)) public investorProjectBalance;

    event InvestmentMade(
        uint256 indexed projectId,
        address indexed investor,
        uint256 amount,
        uint256 tokens
    );
    event InvestmentWithdrawn(
        uint256 indexed projectId,
        address indexed investor,
        uint256 amount
    );

    constructor(address _assetToken, address _projectRegistry) {
        assetToken = AssetToken(payable(_assetToken));
        projectRegistry = ProjectRegistry(_projectRegistry);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function invest(uint256 projectId, uint256 amount) external whenNotPaused {
        require(amount > 0, "Amount must be greater than 0");
        require(
            projectRegistry.isKYCVerified(msg.sender),
            "Investor not KYC verified"
        );

        // Verifica che il progetto esista e sia attivo
        (
            ,  // id
            ,  // name
            ,  // capacity
            ,  // location
            ,  // totalInvestment
            ,  // currentInvestment
            ProjectRegistry.ProjectStatus status,
            address owner,
            // createdAt
        ) = projectRegistry.getProjectDetails(projectId);

        require(owner != address(0), "Project does not exist");
        require(status == ProjectRegistry.ProjectStatus.Active, "Project is not active");

        // Calculate tokens based on investment amount
        uint256 tokens = calculateTokens(amount);

        // Record investment
        projectInvestments[projectId].push(
            Investment({
                projectId: projectId,
                investor: msg.sender,
                amount: amount,
                tokens: tokens,
                timestamp: block.timestamp
            })
        );

        // Update investor balance
        investorProjectBalance[msg.sender][projectId] += tokens;

        // Mint tokens to investor
        assetToken.mint(msg.sender, tokens);

        emit InvestmentMade(projectId, msg.sender, amount, tokens);
    }

    function withdrawInvestment(uint256 projectId, uint256 amount) external whenNotPaused {
        require(amount > 0, "Amount must be greater than 0");

        // Verifica che il progetto esista
        (
            ,  // id
            ,  // name
            ,  // capacity
            ,  // location
            ,  // totalInvestment
            ,  // currentInvestment
            ,  // status
            address owner,
            // createdAt
        ) = projectRegistry.getProjectDetails(projectId);

        require(owner != address(0), "Project does not exist");
        require(
            investorProjectBalance[msg.sender][projectId] >= amount,
            "Insufficient balance"
        );

        // Burn tokens
        assetToken.burn(msg.sender, amount);

        // Update investor balance
        investorProjectBalance[msg.sender][projectId] -= amount;

        emit InvestmentWithdrawn(projectId, msg.sender, amount);
    }

    function getInvestmentDetails(uint256 projectId, address investor)
        external
        view
        returns (
            uint256 totalInvested,
            uint256 tokenBalance,
            uint256 lastInvestmentTime
        )
    {
        Investment[] memory investments = projectInvestments[projectId];
        uint256 total = 0;
        uint256 lastTime = 0;

        for (uint256 i = 0; i < investments.length; i++) {
            if (investments[i].investor == investor) {
                total += investments[i].amount;
                if (investments[i].timestamp > lastTime) {
                    lastTime = investments[i].timestamp;
                }
            }
        }

        return (
            total,
            investorProjectBalance[investor][projectId],
            lastTime
        );
    }

    function calculateTokens(uint256 amount)
        internal
        pure
        returns (uint256)
    {
        // Simple 1:1 ratio for now, can be modified based on project valuation
        return amount;
    }

    function makeInvestment(uint256 projectId) external payable whenNotPaused {
        require(msg.value > 0, "Investment amount must be greater than 0");

        // Verifica che il progetto esista e sia attivo
        (
            ,  // id
            ,  // name
            ,  // capacity
            ,  // location
            ,  // totalInvestment
            ,  // currentInvestment
            ProjectRegistry.ProjectStatus status,
            address owner,
            // createdAt
        ) = projectRegistry.getProjectDetails(projectId);

        require(owner != address(0), "Project does not exist");
        require(status == ProjectRegistry.ProjectStatus.Active, "Project is not active");

        // Verifica che l'investitore sia verificato KYC
        require(projectRegistry.isKYCVerified(msg.sender), "Investor not KYC verified");

        // Calcola i token da emettere
        uint256 tokens = calculateTokens(msg.value);

        // Aggiorna gli investimenti
        projectInvestments[projectId].push(Investment({
            projectId: projectId,
            investor: msg.sender,
            amount: msg.value,
            tokens: tokens,
            timestamp: block.timestamp
        }));

        investorProjectBalance[msg.sender][projectId] += msg.value;

        // Emetti i token
        assetToken.mint(msg.sender, tokens);

        emit InvestmentMade(projectId, msg.sender, msg.value, tokens);
    }
}

contracts/ProjectRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract ProjectRegistry is Ownable, Pausable {
    struct Project {
        uint256 id;
        string name;
        uint256 capacity;
        string location;
        uint256 totalInvestment;
        uint256 currentInvestment;
        ProjectStatus status;
        address owner;
        uint256 createdAt;
    }

    enum ProjectStatus {
        Pending,
        Active,
        Completed,
        Cancelled
    }

    mapping(uint256 => Project) public projects;
    mapping(address => bool) public kycVerified;
    uint256 public projectCount;

    event ProjectRegistered(uint256 indexed projectId, string name, address owner);
    event ProjectStatusUpdated(uint256 indexed projectId, ProjectStatus status);
    event KYCVerified(address indexed investor);

    error ProjectDoesNotExist();
    error InvalidStatus();
    error ProjectCompleted();
    error InvalidAddress();
    error AlreadyVerified();
    error EmptyName();
    error EmptyLocation();
    error ZeroCapacity();

    constructor() {}

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function registerProject(
        string memory name,
        uint256 capacity,
        string memory location
    ) external whenNotPaused {
        // Validate inputs
        if (bytes(name).length == 0) revert EmptyName();
        if (bytes(location).length == 0) revert EmptyLocation();
        if (capacity == 0) revert ZeroCapacity();

        uint256 projectId = projectCount++;
        projects[projectId] = Project({
            id: projectId,
            name: name,
            capacity: capacity,
            location: location,
            totalInvestment: 0,
            currentInvestment: 0,
            status: ProjectStatus.Pending,
            owner: msg.sender,
            createdAt: block.timestamp
        });

        emit ProjectRegistered(projectId, name, msg.sender);
    }

    function updateProjectStatus(uint256 projectId, uint256 newStatus) external {
        if (projectId >= projectCount) revert ProjectDoesNotExist();
        if (newStatus >= 4) revert InvalidStatus();

        require(projects[projectId].owner == msg.sender, "Not project owner");
        require(projects[projectId].status != ProjectStatus.Cancelled, "Project cancelled");
        if (projects[projectId].status == ProjectStatus.Completed) revert ProjectCompleted();

        projects[projectId].status = ProjectStatus(newStatus);
        emit ProjectStatusUpdated(projectId, ProjectStatus(newStatus));
    }

    function verifyKYC(address investor) external onlyOwner {
        if (investor == address(0)) revert InvalidAddress();
        if (kycVerified[investor]) revert AlreadyVerified();

        kycVerified[investor] = true;
        emit KYCVerified(investor);
    }

    function getProjectDetails(uint256 projectId) external view returns (
        uint256 id,
        string memory name,
        uint256 capacity,
        string memory location,
        uint256 totalInvestment,
        uint256 currentInvestment,
        ProjectStatus status,
        address owner,
        uint256 createdAt
    ) {
        if (projectId >= projectCount) revert ProjectDoesNotExist();

        Project memory project = projects[projectId];
        return (
            project.id,
            project.name,
            project.capacity,
            project.location,
            project.totalInvestment,
            project.currentInvestment,
            project.status,
            project.owner,
            project.createdAt
        );
    }

    function isKYCVerified(address investor) external view returns (bool) {
        return kycVerified[investor];
    }
}

contracts/RevenueDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./AssetToken.sol";
import "./InvestmentManager.sol";

contract RevenueDistributor is Ownable, Pausable {
    struct Distribution {
        uint256 projectId;
        uint256 amount;
        uint256 timestamp;
        bool completed;
    }

    AssetToken public assetToken;
    InvestmentManager public investmentManager;
    mapping(uint256 => Distribution[]) public projectDistributions;
    mapping(uint256 => mapping(address => bool)) public claimedDistributions;

    event RevenueReceived(uint256 indexed projectId, uint256 amount);
    event DistributionCompleted(uint256 indexed projectId, uint256 distributionId);
    event RevenueClaimed(
        uint256 indexed projectId,
        address indexed investor,
        uint256 amount
    );

    constructor(address _assetToken, address _investmentManager) {
        assetToken = AssetToken(payable(_assetToken));
        investmentManager = InvestmentManager(_investmentManager);
    }

    function receiveRevenue(uint256 projectId, uint256 amount)
        external
        payable
        whenNotPaused
    {
        require(amount > 0, "Amount must be greater than 0");
        require(msg.value == amount, "Incorrect payment amount");

        // Record the distribution
        uint256 distributionId = projectDistributions[projectId].length;
        projectDistributions[projectId].push(
            Distribution({
                projectId: projectId,
                amount: amount,
                timestamp: block.timestamp,
                completed: false
            })
        );

        emit RevenueReceived(projectId, amount);
    }

    function distributeRevenue(uint256 projectId, uint256 distributionId)
        external
        onlyOwner
        whenNotPaused
    {
        require(
            distributionId < projectDistributions[projectId].length,
            "Invalid distribution ID"
        );
        Distribution storage distribution = projectDistributions[projectId][distributionId];
        require(!distribution.completed, "Distribution already completed");

        // Calculate total tokens for the project
        uint256 totalTokens = assetToken.totalSupply();
        require(totalTokens > 0, "No tokens in circulation");

        // Mark distribution as completed
        distribution.completed = true;

        emit DistributionCompleted(projectId, distributionId);
    }

    function claimRevenue(uint256 projectId, uint256 distributionId)
        external
        whenNotPaused
    {
        require(
            distributionId < projectDistributions[projectId].length,
            "Invalid distribution ID"
        );
        Distribution storage distribution = projectDistributions[projectId][distributionId];
        require(distribution.completed, "Distribution not completed");
        require(
            !claimedDistributions[projectId][msg.sender],
            "Already claimed"
        );

        // Get investor's token balance
        uint256 tokenBalance = assetToken.balanceOf(msg.sender);
        require(tokenBalance > 0, "No tokens to claim");

        // Calculate share based on token balance
        uint256 totalTokens = assetToken.totalSupply();
        uint256 share = (distribution.amount * tokenBalance) / totalTokens;

        // Mark as claimed
        claimedDistributions[projectId][msg.sender] = true;

        // Transfer share to investor
        payable(msg.sender).transfer(share);

        emit RevenueClaimed(projectId, msg.sender, share);
    }

    function getDistributionDetails(uint256 projectId, uint256 distributionId)
        external
        view
        returns (
            uint256 amount,
            uint256 timestamp,
            bool completed
        )
    {
        require(
            distributionId < projectDistributions[projectId].length,
            "Invalid distribution ID"
        );
        Distribution memory distribution = projectDistributions[projectId][distributionId];
        return (distribution.amount, distribution.timestamp, distribution.completed);
    }
}

